// src/components/CriarProvaIA.jsx
import React, { useState } from 'react';

const CriarProvaIA = () => {
  const [tema, setTema] = useState('');
  const [mensagem, setMensagem] = useState('');
  const [carregando, setCarregando] = useState(false);

  const gerarProva = async () => {
    if (!tema.trim()) {
      setMensagem('Por favor, insira um tema.');
      return;
    }

    try {
      setCarregando(true);
      setMensagem('Gerando prova com IA...');

      const response = await fetch('/api/provas/gerar-ia', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ tema }),
      });

      if (!response.ok) {
        throw new Error('Erro ao gerar prova');
      }

      const data = await response.json();
      setMensagem(`Prova "${data.titulo}" criada com sucesso!`);
      setTema('');
    } catch (error) {
      setMensagem('Erro ao gerar a prova.');
    } finally {
      setCarregando(false);
    }
  };

  return (
    <div className="dashboard-card">
      <h3>ðŸ“˜ Criar Prova com IA</h3>
      <p>Digite um tema para gerar uma nova prova automaticamente.</p>
      <input
        type="text"
        placeholder="Ex: RevoluÃ§Ã£o Francesa"
        value={tema}
        onChange={(e) => setTema(e.target.value)}
        style={{
          padding: '10px',
          width: '100%',
          marginBottom: '15px',
          borderRadius: '8px',
          border: '1px solid #ccc',
        }}
      />
      <button onClick={gerarProva} disabled={carregando}>
        {carregando ? 'Gerando...' : 'Gerar Prova'}
      </button>
      {mensagem && <p style={{ marginTop: '15px', color: '#333' }}>{mensagem}</p>}
    </div>
  );
};

export default CriarProvaIA;
